import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [backendStatus, setBackendStatus] = useState('Loading...');
  const [testData, setTestData] = useState(null);

  useEffect(() => {
    // Test backend connection
    fetch('/api/test')
      .then(response => response.json())
      .then(data => {
        setTestData(data);
        setBackendStatus('Connected ‚úÖ');
      })
      .catch(error => {
        console.error('Backend connection failed:', error);
        setBackendStatus('Failed ‚ùå');
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>üöÄ HRMS System</h1>
        <p>Human Resource Management System</p>
      </header>

      <main className="container">
        <div className="card">
          <h2>System Status</h2>
          <div className="status-grid">
            <div className="status-item">
              <strong>Frontend:</strong> Running ‚úÖ
            </div>
            <div className="status-item">
              <strong>Backend:</strong> {backendStatus}
            </div>
          </div>
        </div>

        {testData && (
          <div className="card">
            <h2>Backend Test Response</h2>
            <pre>{JSON.stringify(testData, null, 2)}</pre>
          </div>
        )}

        <div className="card">
          <h2>Next Steps</h2>
          <ul>
            <li>‚úÖ Basic project structure created</li>
            <li>‚úÖ Backend server running</li>
            <li>‚úÖ Frontend React app running</li>
            <li>‚è≥ Database setup (PostgreSQL)</li>
            <li>‚è≥ Authentication system</li>
            <li>‚è≥ Employee management module</li>
            <li>‚è≥ Dashboard implementation</li>
          </ul>
        </div>
      </main>
    </div>
  );
}

export default App;
