import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';

function App() {
  const [backendStatus, setBackendStatus] = useState('Loading...');
  const [databaseStatus, setDatabaseStatus] = useState('Loading...');
  const [testData, setTestData] = useState(null);
  const [healthData, setHealthData] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(true);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');

    if (token && savedUser) {
      setUser(JSON.parse(savedUser));
      setIsAuthenticated(true);
    }

    // Test backend connection
    fetch('/api/test')
      .then(response => response.json())
      .then(data => {
        setTestData(data);
        setBackendStatus('Connected ✅');
      })
      .catch(error => {
        console.error('Backend connection failed:', error);
        setBackendStatus('Failed ❌');
      });

    // Test health endpoint (includes database status)
    fetch('/api/health')
      .then(response => response.json())
      .then(data => {
        setHealthData(data);
        setDatabaseStatus(data.database === 'connected' ? 'Connected ✅' : 'Disconnected ❌');
      })
      .catch(error => {
        console.error('Health check failed:', error);
        setDatabaseStatus('Failed ❌');
      });
  }, []);

  const handleLogin = (userData, token) => {
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleRegister = (userData, token) => {
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  const testDatabase = async () => {
    try {
      const response = await fetch('/api/db-test');
      const data = await response.json();
      if (data.status === 'success') {
        setDatabaseStatus('Connected ✅');
        alert('Database connection successful!');
      } else {
        setDatabaseStatus('Failed ❌');
        alert('Database connection failed!');
      }
    } catch (error) {
      setDatabaseStatus('Failed ❌');
      alert('Database test failed!');
    }
  };

  const initDatabase = async () => {
    try {
      const response = await fetch('/api/init-db', { method: 'POST' });
      const data = await response.json();
      if (data.status === 'success') {
        alert('Database initialized successfully!');
      } else {
        alert('Database initialization failed!');
      }
    } catch (error) {
      alert('Database initialization failed!');
    }
  };

  // Show authentication forms if not authenticated
  if (!isAuthenticated) {
    return (
      <div className="App">
        {showLogin ? (
          <Login 
            onLogin={handleLogin} 
            onSwitchToRegister={() => setShowLogin(false)} 
          />
        ) : (
          <Register 
            onRegister={handleRegister} 
            onSwitchToLogin={() => setShowLogin(true)} 
          />
        )}
      </div>
    );
  }

  // Show dashboard if authenticated
  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div>
            <h1>🚀 HRMS System</h1>
            <p>Human Resource Management System</p>
          </div>
          <div className="user-info">
            <span>Welcome, {user?.first_name} {user?.last_name}</span>
            <button className="btn btn-secondary" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="container">
        <div className="card">
          <h2>System Status</h2>
          <div className="status-grid">
            <div className="status-item">
              <strong>Frontend:</strong> Running ✅
            </div>
            <div className="status-item">
              <strong>Backend:</strong> {backendStatus}
            </div>
            <div className="status-item">
              <strong>Database:</strong> {databaseStatus}
            </div>
            <div className="status-item">
              <strong>Authentication:</strong> Connected ✅
            </div>
          </div>

          <div className="action-buttons">
            <button className="btn btn-primary" onClick={testDatabase}>
              Test Database Connection
            </button>
            <button className="btn btn-secondary" onClick={initDatabase}>
              Initialize Database
            </button>
          </div>
        </div>

        <div className="card">
          <h2>User Information</h2>
          <div className="user-details">
            <p><strong>Name:</strong> {user?.first_name} {user?.last_name}</p>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>Role:</strong> {user?.role}</p>
            <p><strong>Department:</strong> {user?.department || 'Not assigned'}</p>
            <p><strong>Position:</strong> {user?.position || 'Not assigned'}</p>
          </div>
        </div>

        {testData && (
          <div className="card">
            <h2>Backend Test Response</h2>
            <pre>{JSON.stringify(testData, null, 2)}</pre>
          </div>
        )}

        {healthData && (
          <div className="card">
            <h2>Health Check Response</h2>
            <pre>{JSON.stringify(healthData, null, 2)}</pre>
          </div>
        )}

        <div className="card">
          <h2>Next Steps</h2>
          <ul>
            <li>✅ Basic project structure created</li>
            <li>✅ Backend server running</li>
            <li>✅ Frontend React app running</li>
            <li>✅ Environment variables configured</li>
            <li>✅ PostgreSQL connected</li>
            <li>✅ Database tables created</li>
            <li>✅ Authentication system working</li>
            <li>⏳ Employee management module</li>
            <li>⏳ Dashboard implementation</li>
            <li>⏳ Attendance tracking</li>
            <li>⏳ Leave management</li>
          </ul>
        </div>
      </main>
    </div>
  );
}

export default App;
