import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';

function App() {
  const [backendStatus, setBackendStatus] = useState('Loading...');
  const [databaseStatus, setDatabaseStatus] = useState('Loading...');
  const [testData, setTestData] = useState(null);
  const [healthData, setHealthData] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);
  const [showLogin, setShowLogin] = useState(true);

  useEffect(() => {
    // Check if user is already logged in
    const savedToken = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');

    if (savedToken && savedUser) {
      // Validate token by making a test API call
      fetch('/api/health', {
        headers: {
          'Authorization': `Bearer ${savedToken}`
        }
      })
      .then(response => {
        if (response.ok) {
          setUser(JSON.parse(savedUser));
          setToken(savedToken);
          setIsAuthenticated(true);
        } else {
          // Token is invalid, clear storage
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          setIsAuthenticated(false);
        }
      })
      .catch(error => {
        console.error('Token validation failed:', error);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setIsAuthenticated(false);
      });
    }

    // Test backend connection
    fetch('/api/test')
      .then(response => response.json())
      .then(data => {
        setTestData(data);
        setBackendStatus('Connected ✅');
      })
      .catch(error => {
        console.error('Backend connection failed:', error);
        setBackendStatus('Failed ❌');
      });

    // Test health endpoint (includes database status)
    fetch('/api/health')
      .then(response => response.json())
      .then(data => {
        setHealthData(data);
        setDatabaseStatus(data.database === 'connected' ? 'Connected ✅' : 'Disconnected ❌');
      })
      .catch(error => {
        console.error('Health check failed:', error);
        setDatabaseStatus('Failed ❌');
      });
  }, []);

  const handleLogin = (userData, userToken) => {
    setUser(userData);
    setToken(userToken);
    setIsAuthenticated(true);
  };

  const handleRegister = (userData, userToken) => {
    setUser(userData);
    setToken(userToken);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setToken(null);
    setIsAuthenticated(false);
  };

  // Show authentication forms if not authenticated
  if (!isAuthenticated) {
    return (
      <div className="App">
        {showLogin ? (
          <Login 
            onLogin={handleLogin} 
            onSwitchToRegister={() => setShowLogin(false)} 
          />
        ) : (
          <Register 
            onRegister={handleRegister} 
            onSwitchToLogin={() => setShowLogin(true)} 
          />
        )}
      </div>
    );
  }

  // Show dashboard if authenticated
  return (
    <div className="App">
      <Dashboard user={user} onLogout={handleLogout} token={token} />
    </div>
  );
}

export default App;
